package ch.hsr.univote.unigen.tasks;

import ch.bfh.univote.common.SignatureParameters;
import ch.hsr.univote.unigen.VoteGenerator;
import ch.hsr.univote.unigen.db.DB4O;
import ch.hsr.univote.unigen.helper.ConfigHelper;
import java.math.BigInteger;

/**
 *
 * @author Gian Polt√©ra
 */
public class SignatureParametersTask extends VoteGenerator {

    public void run() throws Exception {
        /*create SignatureParameters*/
        SignatureParameters signatureParameters = createSignatureParameters();
        
        /*submit to ElectionBoard*/
        electionBoard.signatureParameters = signatureParameters;
        
        /*save in db*/
        DB4O.storeDB(ConfigHelper.getElectionId(), signatureParameters);
    }
    
    private SignatureParameters createSignatureParameters() {
        SignatureParameters signatureParameters = new SignatureParameters();
        //Schnorr Signautre, Prime p
        signatureParameters.setPrime(new BigInteger(""));
        //Schnorr Signature, GroupOrder q
        signatureParameters.setGroupOrder(new BigInteger("65133683824381501983523684796057614145070427752690897588060462960319251776021"));
        //Schnorr Signature, Generator g
        signatureParameters.setGenerator(new BigInteger(""));

        return signatureParameters;
    }
}
